import logo from './logo.svg';
import './App.css';
import React from 'react';
import { getPokemons } from './API';


class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      pokemons: null,
    }
  }

  componentDidMount() {
    this.loadPokemons();
  }

  async loadPokemons() {
    try {
      setTimeout(async () => {
        const pokemonsFromAPI = await getPokemons();
        this.setState({ pokemons: pokemonsFromAPI})
      }, 5000)
    } catch (error) {
      console.error(error);
    }
  }

  renderPokemons() {
    const { pokemons } = this.state;
    if (pokemons === null) {
      return <p>Loading...</p>
    } else {
      return pokemons.map((pokemon, index) => {
        const imageUrl = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemon.numero}.png`

        return
          <div>
            <img src={imageUrl}></img>
            <p key={index}>{pokemon.nom}{pokemon.numero}</p>
          </div>
      })
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src="https://cdn.bulbagarden.net/upload/4/4b/Pok%C3%A9dex_logo.png" className="App-logo" alt="logo" />
          <p>
            Projet pokedex
          </p>
          {this.renderPokemons()}
        </header>
      </div>
    );
  }
}

export default App;
