import logo from './logo.svg';
import './App.css';
import React from 'react';
import { getPokemons } from './API';


class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      pokemons: null,
      show: 'pokemonDetails',
    }
  }

  componentDidMount() {
    this.loadPokemons();
  }

  async loadPokemons() {
    try {
      setTimeout(async () => {
        const pokemonsFromAPI = await getPokemons();
        this.setState({ pokemons: pokemonsFromAPI})
      }, 1000)
    } catch (error) {
      console.error(error);
    }
  }

  renderPokemons() {
    const { pokemons } = this.state;
    if (pokemons === null) {
      return <p>Loading...</p>
    } else {
      return pokemons.map((pokemon, index) => {
        const imageUrl = `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemon.numero}.png`

        return (
          <div>
            <img src={imageUrl}></img>
            <p key={index}>{pokemon.numero} #{pokemon.nom}</p>
          </div>
        )
      })
    }
  }

  renderPokemonDetails() {
    return <p>Détails du pokémon</p>
  }

  render() {
    const { show } = this.state

    return (
      <div className="App">
        <header className="App-header">
          <img src="https://cdn.bulbagarden.net/upload/4/4b/Pok%C3%A9dex_logo.png" className="App-logo" alt="logo" />
          {
            show === 'allPokemons' ? this.renderPokemons() : this.renderPokemonDetails()
          }
        </header>
      </div>
    );
  }
}

export default App;
