import logo from './logo.svg';
import './App.css';
import React from 'react';
import { getPokemons } from './API';


class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      pokemons: null,
    }
  }

  componentDidMount() {
    this.loadPokemons();
  }

  async loadPokemons() {
    try {
      const pokemonsFromAPI = await getPokemons();
      this.setState({ pokemons: pokemonsFromAPI})
    } catch (error) {
      console.error(error);
    }
  }

  render() {
    const { pokemons } = this.state
    return (
      <div className="App">
        <header className="App-header">
          <img src="https://cdn.bulbagarden.net/upload/4/4b/Pok%C3%A9dex_logo.png" className="App-logo" alt="logo" />
          <p>
            Projet pokedex
          </p>
          {
            pokemons !== null ?
              <p>Les pokémons éxistent</p> :
              <p>Impossible de charger les pokémons</p> 
          }
        </header>
      </div>
    );
  }
}

export default App;
