import logo from './logo.svg';
import './App.css';
import React from 'react';
import { getPokemons } from './API';


function getPokemonImageURL(pokemonNumber) {
  return `https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${pokemonNumber}.png`
}

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      pokemons: null,
      show: 'allPokemons',
      pokemonNumber: null,
    }
  }

  componentDidMount() {
    this.loadPokemons();
  }

  async loadPokemons() {
    try {
      setTimeout(async () => {
        const pokemonsFromAPI = await getPokemons();
        this.setState({ pokemons: pokemonsFromAPI})
      }, 1000)
    } catch (error) {
      console.error(error);
    }
  }

  renderPokemons() {
    const { pokemons } = this.state;
    if (pokemons === null) {
      return <p>Loading...</p>
    } else {
      return pokemons.map((pokemon, index) => {

        return (
          <div key={index}>
            <img src={getPokemonImageURL(pokemon.numero)}></img>
            <p>#{pokemon.numero} {pokemon.nom}</p>
            <button onClick={() => this.setState({ show: 'pokemonDetails', pokemonNumber: pokemon.numero })}>Show details</button>
          </div>
        )
      })
    }
  }

  renderPokemonDetails() {
    const { pokemonNumber } = this.state;

    console.log('pokemonNumber', pokemonNumber)
    return (
      <div>
        <img src={getPokemonImageURL(pokemonNumber)}></img>
        <p>Voici les d√©tails du pokemon {pokemonNumber}</p>
        <button onClick={() => this.setState({ show: 'allPokemons'})}>Liste pokemons</button>
      </div>
    )
  }

  render() {
    const { show } = this.state

    return (
      <div className="App">
        <header className="App-header">
          <img src="https://cdn.bulbagarden.net/upload/4/4b/Pok%C3%A9dex_logo.png" className="App-logo" alt="logo" />
          {
            show === 'allPokemons' ? this.renderPokemons() : this.renderPokemonDetails()
          }
        </header>
      </div>
    );
  }
}

export default App;
